// src/Components/Admin/Settings.js
import React, { useState, useEffect } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

// simple translations dictionary
const translations = {
  en: {
    back: "â¬… Back",
    title: "âš™ï¸ Admin Settings",
    security: "ðŸ”‘ Security",
    newPassword: "New Password",
    updatePassword: "Update Password",
    twoFA: "Enable Two-Factor Authentication (2FA)",
    signOutAll: "Sign Out of All Devices",
    preferences: "ðŸŽ¨ Preferences",
    theme: "Theme",
    panelTitle: "Admin Panel Title",
    logoURL: "Logo URL",
    emailNotif: "Enable Email Notifications",
    systemControls: "ðŸ›  System Controls",
    maintenanceMode: "Enable Maintenance Mode",
    autoApprove: "Auto-Approve Applications",
    exportData: "Export User Data",
    saveSettings: "Save Settings",
    language: "Language",
  },
  ph: {
    back: "â¬… Bumalik",
    title: "âš™ï¸ Mga Setting ng Admin",
    security: "ðŸ”‘ Seguridad",
    newPassword: "Bagong Password",
    updatePassword: "I-update ang Password",
    twoFA: "I-enable ang Dalawang Hakbang na Pagpapatunay (2FA)",
    signOutAll: "Mag-Sign Out sa Lahat ng Device",
    preferences: "ðŸŽ¨ Mga Kagustuhan",
    theme: "Tema",
    panelTitle: "Pamagat ng Admin Panel",
    logoURL: "URL ng Logo",
    emailNotif: "I-enable ang Email Notifications",
    systemControls: "ðŸ›  Mga Kontrol ng Sistema",
    maintenanceMode: "I-enable ang Maintenance Mode",
    autoApprove: "Awtomatikong Aprubahan ang Mga Aplikasyon",
    exportData: "I-export ang Data ng User",
    saveSettings: "I-save ang Mga Setting",
    language: "Wika",
  },
};

function Settings({ setTheme, theme }) {
  const [password, setPassword] = useState("");
  const [notifications, setNotifications] = useState(true);
  const [maintenanceMode, setMaintenanceMode] = useState(false);
  const [twoFA, setTwoFA] = useState(false);
  const [panelTitle, setPanelTitle] = useState("Laundry Scout Admin");
  const [panelLogo, setPanelLogo] = useState("https://via.placeholder.com/100");
  const [autoApprove, setAutoApprove] = useState(false);
  const [language, setLanguage] = useState("en");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // âœ… Load settings on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);

    const savedTitle =
      localStorage.getItem("panelTitle") || "Laundry Scout Admin";
    const savedLogo =
      localStorage.getItem("panelLogo") || "https://via.placeholder.com/100";
    const savedAutoApprove = localStorage.getItem("autoApprove") === "true";
    const savedTwoFA = localStorage.getItem("twoFA") === "true";
    const savedLang = localStorage.getItem("language") || "en";

    setPanelTitle(savedTitle);
    setPanelLogo(savedLogo);
    setAutoApprove(savedAutoApprove);
    setTwoFA(savedTwoFA);
    setLanguage(savedLang);
  }, []);

  // âœ… Save all settings
  const handleSaveSettings = () => {
    document.documentElement.setAttribute("data-theme", theme);
    localStorage.setItem("theme", theme);
    localStorage.setItem("panelTitle", panelTitle);
    localStorage.setItem("panelLogo", panelLogo);
    localStorage.setItem("autoApprove", autoApprove);
    localStorage.setItem("twoFA", twoFA);
    localStorage.setItem("language", language);

    setMessage("âœ… Settings saved & applied globally!");
  };

  // âœ… Update password
  const handlePasswordUpdate = async () => {
    if (!password) {
      setMessage("âŒ Please enter a new password.");
      return;
    }
    const { error } = await supabase.auth.updateUser({ password });
    if (error) {
      setMessage("âŒ Error: " + error.message);
    } else {
      setMessage("âœ… Password updated successfully!");
      setPassword("");
    }
  };

  // âœ… Sign out all sessions
  const handleSignOutAll = async () => {
    const { error } = await supabase.auth.signOut();
    if (error) {
      setMessage("âŒ Error signing out: " + error.message);
    } else {
      setMessage("âœ… Signed out of all sessions.");
    }
  };

  // âœ… Export data as JSON
  const handleExportData = async () => {
    const { data, error } = await supabase.from("user_profiles").select("*");
    if (error) {
      setMessage("âŒ Export failed: " + error.message);
      return;
    }
    const blob = new Blob([JSON.stringify(data, null, 2)], {
      type: "application/json",
    });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "user_profiles.json";
    a.click();
    URL.revokeObjectURL(url);
    setMessage("âœ… Data exported successfully!");
  };

  const t = translations[language];

  // Update theme both locally and in parent
  const handleThemeChange = (e) => {
    setTheme(e.target.value);
  };

  return (
    <div className="page-container">
      {/* Back Button */}
      <button className="btn" onClick={() => navigate(-1)}>
        {t.back}
      </button>

      <h2>{t.title}</h2>

      {/* Security */}
      <div className="card">
        <h3>{t.security}</h3>
        <div className="form-group">
          <label>{t.newPassword}</label>
          <input
            type="password"
            placeholder={t.newPassword}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="btn danger" onClick={handlePasswordUpdate}>
          {t.updatePassword}
        </button>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={twoFA}
            onChange={() => setTwoFA(!twoFA)}
          />
          <label>{t.twoFA}</label>
        </div>
        <button className="btn" onClick={handleSignOutAll}>
          {t.signOutAll}
        </button>
      </div>

      {/* Preferences */}
      <div className="card">
        <h3>{t.preferences}</h3>
        <div className="form-group">
          <label>{t.theme}</label>
          <select value={theme} onChange={handleThemeChange}>
            <option value="light">ðŸŒž Light</option>
            <option value="dark">ðŸŒ™ Dark</option>
          </select>
        </div>
        <div className="form-group">
          <label>{t.panelTitle}</label>
          <input
            type="text"
            value={panelTitle}
            onChange={(e) => setPanelTitle(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>{t.logoURL}</label>
          <input
            type="text"
            value={panelLogo}
            onChange={(e) => setPanelLogo(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>{t.language}</label>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="ph">Filipino</option>
          </select>
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={notifications}
            onChange={() => setNotifications(!notifications)}
          />
          <label>{t.emailNotif}</label>
        </div>
      </div>

      {/* System Controls */}
      <div className="card">
        <h3>{t.systemControls}</h3>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={maintenanceMode}
            onChange={() => setMaintenanceMode(!maintenanceMode)}
          />
          <label>{t.maintenanceMode}</label>
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={autoApprove}
            onChange={() => setAutoApprove(!autoApprove)}
          />
          <label>{t.autoApprove}</label>
        </div>
        <button className="btn" onClick={handleExportData}>
          {t.exportData}
        </button>
        <button className="btn primary" onClick={handleSaveSettings}>
          {t.saveSettings}
        </button>
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Settings;
