import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import { handleLogout } from "./Components/Services/Logout";
import { supabase } from "./Supabase/supabaseClient";

// Import your components
import Dashboard from "./Components/Dashboard";
import Users from "./Components/Users";
import Applications from "./Components/Applications";
import Clients from "./Components/Clients";
import History from "./Components/History";
import Feedback from "./Components/Feedback";
import ClientDetails from "./Components/Details/ClientDetails";
import Profile from "./Components/settings/Profile";
import Settings from "./Components/settings/Settings";

// Splash screen
function SplashScreen() {
  return (
    <div className="app-bg">
      <div className="splash-screen">
        <div className="splash-logo">
          <img src="/lslogo.png" alt="Laundry Scout Logo" width="100" height="100" />
        </div>
        <h1 className="splash-title">Laundry Scout</h1>
      </div>
    </div>
  );
}

// Change Credentials component
function ChangeCredentials({ onBack }) {
  const [step, setStep] = useState(1); // 1 = request OTP, 2 = verify OTP
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [newUsername, setNewUsername] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  // Step 1: request OTP
  const requestOtp = async (e) => {
    e.preventDefault();
    const { error } = await supabase.auth.signInWithOtp({ email });
    if (error) {
      setError("Failed to send OTP");
      setMessage("");
    } else {
      setMessage("OTP sent to your email");
      setError("");
      setStep(2);
    }
  };

  // Step 2: verify OTP & update credentials
  const verifyAndChange = async (e) => {
    e.preventDefault();
    const { data, error: verifyError } = await supabase.auth.verifyOtp({
      email,
      token: otp,
      type: "email",
    });

    if (verifyError || !data?.user) {
      setError("Invalid OTP");
      setMessage("");
      return;
    }

    // Update username/password in admin table
    const { error: updateError } = await supabase
      .from("admin")
      .update({ username: newUsername, password: newPassword })
      .eq("email", email);

    if (updateError) {
      setError("Failed to update credentials");
      setMessage("");
    } else {
      setMessage("Credentials updated successfully");
      setError("");
    }
  };

  return (
    <div className="app-bg">
      <div className="reset-container">
        <h2>Change Credentials</h2>
        {step === 1 && (
          <form onSubmit={requestOtp} className="reset-form">
            <input
              type="email"
              placeholder="Enter your registered email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <button type="submit">Send OTP</button>
          </form>
        )}
        {step === 2 && (
          <form onSubmit={verifyAndChange} className="reset-form">
            <input
              type="text"
              placeholder="OTP code"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="New Username"
              value={newUsername}
              onChange={(e) => setNewUsername(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="New Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <button type="submit">Update Credentials</button>
          </form>
        )}
        {error && <div className="reset-error">{error}</div>}
        {message && <div className="reset-message">{message}</div>}
        <button className="back-btn" onClick={onBack}>Back</button>
      </div>
    </div>
  );
}

// Forgot Password (kept from your code)
function ForgotPassword({ onBack }) {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  const handleReset = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase
      .from("admin")
      .select("email")
      .eq("email", email)
      .single();

    if (error || !data) {
      setError("Email not found");
      setMessage("");
      return;
    }

    const tempPassword = Math.random().toString(36).slice(-8);
    const { error: updateError } = await supabase
      .from("admin")
      .update({ password: tempPassword })
      .eq("email", email);

    if (updateError) {
      setError("Error resetting password");
      setMessage("");
    } else {
      setMessage(`Temporary password: ${tempPassword}`);
      setError("");
    }
  };

  return (
    <div className="app-bg">
      <div className="login-container">
        <h2>Reset Password</h2>
        <form onSubmit={handleReset}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <button type="submit">Reset Password</button>
        </form>
        {error && <div style={{ color: "red" }}>{error}</div>}
        {message && <div style={{ color: "green" }}>{message}</div>}
        <button onClick={onBack} style={{ marginTop: "10px" }}>
          Back to Login
        </button>
      </div>
    </div>
  );
}

// Admin login
function AdminLogin({ onLogin }) {
  const [username, setUsername] = useState("admin");
  const [password, setPassword] = useState("admin");
  const [error, setError] = useState("");
  const [showForgot, setShowForgot] = useState(false);
  const [showChange, setShowChange] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { data, error } = await supabase
      .from("admin")
      .select("*")
      .eq("username", username)
      .eq("password", password)
      .single();

    if (error || !data) {
      setError("Invalid username or password");
    } else {
      setError("");
      onLogin();
    }
  };

  if (showForgot) {
    return <ForgotPassword onBack={() => setShowForgot(false)} />;
  }

  if (showChange) {
    return <ChangeCredentials onBack={() => setShowChange(false)} />;
  }

  return (
    <div className="app-bg">
      <div className="login-container">
        <div className="login-logo">
          <img src="/lslogo.png" alt="Laundry Scout Logo" width="60" height="60" />
        </div>
        <h2 className="login-title">Welcome Admin</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username or email"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
          <div className="forgot-password">
            <a href="#" onClick={() => setShowForgot(true)}>Forgot Password?</a>
          </div>
          <div className="forgot-password">
            <a href="#" onClick={() => setShowChange(true)}>Change Username/Password</a>
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

// Main App
function App() {
  const [showSplash, setShowSplash] = useState(true);
  const [loggedIn, setLoggedIn] = useState(false);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const timer = setTimeout(() => setShowSplash(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    if (loggedIn) {
      fetchUsers();
    }
  }, [loggedIn]);

  async function fetchUsers() {
    const { data, error } = await supabase.from("users").select("*");
    if (error) {
      console.error("Error fetching users:", error);
    } else {
      setUsers(data);
    }
  }

  const handleUserLogout = async () => {
    await handleLogout(() => setLoggedIn(false));
  };

  if (showSplash) return <SplashScreen />;
  if (!loggedIn) return <AdminLogin onLogin={() => setLoggedIn(true)} />;

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Navigate to="/dashboard" replace />} />
        <Route path="/dashboard" element={<Dashboard users={users} onLogout={handleUserLogout} />} />
        <Route path="/users" element={<Users />} />
        <Route path="/applications" element={<Applications />} />
        <Route path="/clients" element={<Clients />} />
        <Route path="/clients/:id" element={<ClientDetails />} />
        <Route path="/history" element={<History />} />
        <Route path="/feedback" element={<Feedback />} />
        <Route path="/settings" element={<Settings />} />
        <Route path="/profile" element={<Profile />} />
        <Route path="*" element={<h2>Page Not Found</h2>} />
      </Routes>
    </Router>
  );
}

export default App;
