import React, { useState, useEffect, useRef } from "react";
import { supabase } from "../Supabase/supabaseClient";
import "../Style/Applications.css";
import {
  FiSettings,
  FiBell,
  FiMenu,
  FiHome,
  FiFileText,
  FiUsers,
  FiUser,
  FiClock,
  FiMessageSquare,
  FiLogOut,
} from "react-icons/fi";
import { Link, useNavigate } from "react-router-dom";
import Notifications from "./Notifications"; // âœ… Import Notifications Component

function Applications({ onLogout }) {
  const [businesses, setBusinesses] = useState([]);
  const [loading, setLoading] = useState(true);

  const [search, setSearch] = useState("");
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  const [selectedBiz, setSelectedBiz] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // Settings & Notifications
  const [showSettings, setShowSettings] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  const navigate = useNavigate();
  const searchRef = useRef(null);

  // Fetch businesses and notifications on mount
  useEffect(() => {
    fetchBusinesses();
    fetchNotifications();
  }, []);

  // Fetch Businesses
  const fetchBusinesses = async () => {
    setLoading(true);
    const { data, error } = await supabase.from("business_profiles").select("*");

    if (error) {
      console.error("Error fetching businesses:", error.message);
    } else {
      setBusinesses(data || []);
    }
    setLoading(false);
  };

  // Fetch Notifications (Users + Businesses)
  const fetchNotifications = async () => {
    try {
      const { data: users, error: userError } = await supabase
        .from("user_profiles")
        .select("id, username, created_at")
        .order("created_at", { ascending: false });

      const { data: businesses, error: bizError } = await supabase
        .from("business_profiles")
        .select("id, business_name, owner_first_name, owner_last_name, created_at")
        .order("created_at", { ascending: false });

      if (userError) throw userError;
      if (bizError) throw bizError;

      const userNotifications =
        users?.map((u) => ({
          id: `user-${u.id}`,
          title: "New user registered",
          message: u.username,
          createdAt: u.created_at,
        })) || [];

      const businessNotifications =
        businesses?.map((b) => ({
          id: `biz-${b.id}`,
          title: "New business registered",
          message: `${b.business_name} (Owner: ${b.owner_first_name} ${b.owner_last_name})`,
          createdAt: b.created_at,
        })) || [];

      const allNotifications = [...userNotifications, ...businessNotifications].sort(
        (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
      );

      setNotifications(allNotifications);
      setUnreadCount(allNotifications.length);
    } catch (err) {
      console.error("Error fetching notifications:", err.message);
    }
  };

  // Handle search + history
  const handleKeyDown = (e) => {
    if (e.key === "Enter" && search.trim() !== "") {
      if (!history.includes(search)) {
        setHistory([search, ...history].slice(0, 5));
      }
      setShowHistory(false);
    }
  };

  const deleteHistory = (item) => {
    setHistory(history.filter((h) => h !== item));
  };

  const filteredBusinesses = businesses.filter((biz) =>
    [
      biz.business_name,
      biz.owner_first_name,
      biz.owner_last_name,
      biz.email,
      biz.business_address,
      biz.business_phone_number,
    ]
      .filter(Boolean)
      .some((field) => field.toLowerCase().includes(search.toLowerCase()))
  );

  return (
    <div className="dashboard-root">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "open" : "closed"}`}>
        <div className="sidebar-title">Laundry Scout</div>
        <nav>
          <ul>
            <li>
              <Link to="/dashboard">
                <FiHome className="menu-icon" />
                <span>Dashboard</span>
              </Link>
            </li>
            <li className="active">
              <Link to="/applications">
                <FiFileText className="menu-icon" />
                <span>Applications</span>
              </Link>
            </li>
            <li>
              <Link to="/clients">
                <FiUsers className="menu-icon" />
                <span>Clients</span>
              </Link>
            </li>
            <li>
              <Link to="/users">
                <FiUser className="menu-icon" />
                <span>Users</span>
              </Link>
            </li>
            <li>
              <Link to="/history">
                <FiClock className="menu-icon" />
                <span>History</span>
              </Link>
            </li>
            <li>
              <Link to="/feedback">
                <FiMessageSquare className="menu-icon" />
                <span>Feedback</span>
              </Link>
            </li>
          </ul>
        </nav>
        <div className="logout" onClick={onLogout}>
          <FiLogOut className="menu-icon" />
          <span>Log Out</span>
        </div>
      </aside>

      {/* Main */}
      <main className="dashboard-main">
        <div className="applications-container">
          {/* Header */}
          <header className="applications-header">
            <div className="header-left">
              <FiMenu
                className="toggle-sidebar"
                onClick={() => setSidebarOpen(!sidebarOpen)}
              />
              <div>
                <h2>APPLICATIONS</h2>
                <p className="applications-subtitle">
                  All the Applicants Businesses waiting to be reviewed and Approved
                </p>
              </div>
            </div>

            {/* Notifications Component */}
            <Notifications />
          </header>

          {/* Applications Table */}
          {!selectedBiz ? (
            <>
              <div className="applications-filters">
                <div className="filter-tab">
                  <span>
                    All Applicants{" "}
                    <span className="count">{filteredBusinesses.length}</span>
                  </span>
                </div>

                <div className="search-wrapper" ref={searchRef}>
                  <input
                    type="text"
                    placeholder="Search Here"
                    className="search-box"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    onKeyDown={handleKeyDown}
                    onFocus={() => setShowHistory(true)}
                  />
                  {showHistory && history.length > 0 && (
                    <ul className="search-history">
                      {history.map((item, idx) => (
                        <li key={idx} onClick={() => setSearch(item)}>
                          <span>{item}</span>
                          <button
                            className="delete-history"
                            onClick={(e) => {
                              e.stopPropagation();
                              deleteHistory(item);
                            }}
                          >
                            Ã—
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>

              <div className="applications-table-wrapper">
                <h3 className="table-title">Applicants For Review</h3>
                <table className="applications-table">
                  <thead>
                    <tr>
                      <th>Review</th>
                      <th>Store Name</th>
                      <th>Owner Name</th>
                      <th>Phone Number</th>
                      <th>Date Submitted</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {loading ? (
                      <tr>
                        <td colSpan="6">Loading...</td>
                      </tr>
                    ) : filteredBusinesses.length > 0 ? (
                      filteredBusinesses.map((biz, idx) => (
                        <tr key={biz.id || idx}>
                          <td>
                            <button
                              className="review-btn"
                              onClick={() => setSelectedBiz(biz)}
                            >
                              Review
                            </button>
                          </td>
                          <td>{biz.business_name}</td>
                          <td>
                            {biz.owner_first_name || ""}{" "}
                            {biz.owner_last_name || ""}
                          </td>
                          <td>{biz.business_phone_number}</td>
                          <td>
                            {biz.created_at
                              ? new Date(biz.created_at).toLocaleDateString(
                                  "en-US",
                                  {
                                    year: "numeric",
                                    month: "long",
                                    day: "numeric",
                                  }
                                )
                              : ""}
                          </td>
                          <td className={`status ${biz.status || "pending"}`}>
                            {biz.status || "Pending"}
                          </td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="6">No businesses found.</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </>
          ) : (
            <div className="review-panel">
              <h3>{selectedBiz.business_name} For Review</h3>
              <button className="back-btn" onClick={() => setSelectedBiz(null)}>
                Back
              </button>
            </div>
          )}
        </div>
      </main>

      {/* Full Notifications Overlay */}
      {showNotifications === "all" && (
        <div className="overlay">
          <div className="overlay-content">
            <div className="overlay-header">
              <h3>All Notifications</h3>
              <button
                className="close-btn"
                onClick={() => setShowNotifications(false)}
              >
                Ã—
              </button>
            </div>
            <div className="overlay-list">
              {notifications.map((n) => (
                <div key={n.id} className="overlay-item">
                  <strong>{n.title}</strong>
                  <p>{n.message}</p>
                  <span className="notif-time">
                    {new Date(n.createdAt).toLocaleString()}
                  </span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Applications;
