// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import "./App.css";
import { supabase } from "./Supabase/supabaseClient";

// Import components
import Dashboard from "./Components/Dashboard";
import Users from "./Components/Users";
import Applications from "./Components/Applications";
import Clients from "./Components/Clients";
import History from "./Components/History";
import Feedback from "./Components/Feedback";
import ClientDetails from "./Components/Details/ClientDetails";
import Profile from "./Components/settings/Profile";
import Settings from "./Components/settings/Settings";
import ForgotPassword from "./Components/Auth/ForgotPassword";
import ResetPassword from "./Components/Auth/ResetPassword";

// ✅ Splash screen
function SplashScreen() {
  return (
    <div className="app-bg">
      <div className="splash-screen">
        <div className="splash-logo">
          <img src="/lslogo.png" alt="Laundry Scout Logo" width="100" height="100" />
        </div>
        <h1 className="splash-title">Laundry Scout</h1>
      </div>
    </div>
  );
}

// ✅ Admin login
function AdminLogin({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { data: admin, error } = await supabase
      .from("admin")
      .select("*")
      .eq("username", username)
      .eq("password", password) // ⚠️ Replace with hashed password in production
      .maybeSingle();

    if (error || !admin) {
      setError("❌ Invalid username or password");
    } else {
      setError("");
      if (rememberMe) {
        localStorage.setItem("adminUser", JSON.stringify(admin));
      } else {
        sessionStorage.setItem("adminUser", JSON.stringify(admin));
      }
      onLogin(admin);
    }
  };

  return (
    <div className="app-bg">
      <div className="login-container">
        <div className="login-logo">
          <img src="/lslogo.png" alt="Laundry Scout Logo" width="60" height="60" />
        </div>
        <h2 className="login-title">Welcome Admin</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <label className="remember-me">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
            />{" "}
            Remember me
          </label>

          {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}
          <div className="forgot-password">
            <a href="/forgot-password">Forgot Password?</a>
          </div>
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

// ✅ Main App
function App() {
  const [showSplash, setShowSplash] = useState(true);
  const [loggedIn, setLoggedIn] = useState(false);
  const [adminUser, setAdminUser] = useState(null);
  const [users, setUsers] = useState([]);

  // Splash timeout
  useEffect(() => {
    const timer = setTimeout(() => setShowSplash(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  // Restore session on mount
  useEffect(() => {
    const savedUser =
      localStorage.getItem("adminUser") || sessionStorage.getItem("adminUser");
    if (savedUser) {
      setAdminUser(JSON.parse(savedUser));
      setLoggedIn(true);
    }
  }, []);

  // Fetch users after login
  useEffect(() => {
    if (loggedIn) {
      fetchUsers();
    }
  }, [loggedIn]);

  async function fetchUsers() {
    const { data, error } = await supabase.from("users").select("*");
    if (!error) setUsers(data);
  }

  // Logout
  const handleLogout = async () => {
    await supabase.auth.signOut();
    setLoggedIn(false);
    setAdminUser(null);
    localStorage.removeItem("adminUser");
    sessionStorage.removeItem("adminUser");
  };

  if (showSplash) return <SplashScreen />;

  if (!loggedIn) {
    return (
      <AdminLogin
        onLogin={(admin) => {
          setLoggedIn(true);
          setAdminUser(admin);
        }}
      />
    );
  }

  return (
    <Router>
      <Routes>
        {/* Force profile update if email is missing */}
        <Route
          path="/"
          element={
            adminUser?.email
              ? <Navigate to="/dashboard" replace />
              : <Navigate to="/profile" replace />
          }
        />

        <Route path="/dashboard" element={<Dashboard users={users} onLogout={handleLogout} />} />
        <Route path="/users" element={<Users onLogout={handleLogout} />} />
        <Route path="/applications" element={<Applications onLogout={handleLogout} />} />
        <Route path="/clients" element={<Clients onLogout={handleLogout} />} />
        <Route path="/clients/:id" element={<ClientDetails onLogout={handleLogout} />} />
        <Route path="/history" element={<History onLogout={handleLogout} />} />
        <Route path="/feedback" element={<Feedback onLogout={handleLogout} />} />
        <Route path="/settings" element={<Settings onLogout={handleLogout} />} />
        <Route path="/profile" element={<Profile adminUser={adminUser} onLogout={handleLogout} />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route path="/reset-password" element={<ResetPassword />} />
        <Route path="*" element={<h2>Page Not Found</h2>} />
      </Routes>
    </Router>
  );
}

export default App;
