import React, { useEffect, useState } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { FiBell } from "react-icons/fi";
import "../../Style/Notifications.css";

function Notifications() {
  const [notifications, setNotifications] = useState([]);
  const [unread, setUnread] = useState(false);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  useEffect(() => {
    // Subscribe to new user signups
    const userSub = supabase
      .channel("user-changes")
      .on(
        "postgres_changes",
        { event: "INSERT", schema: "public", table: "user_profiles" },
        (payload) => {
          const newNotification = {
            type: "user",
            message: `New user registered: ${
              payload.new.username || payload.new.email
            }`,
            time: new Date().toLocaleString(),
          };
          setNotifications((prev) => [newNotification, ...prev]);
          setUnread(true);
        }
      )
      .subscribe();

    // Subscribe to new feedback
    const feedbackSub = supabase
      .channel("feedback-changes")
      .on(
        "postgres_changes",
        { event: "INSERT", schema: "public", table: "feedback" },
        (payload) => {
          const newNotification = {
            type: "feedback",
            message: `New feedback received: "${payload.new.feedback}"`,
            time: new Date().toLocaleString(),
          };
          setNotifications((prev) => [newNotification, ...prev]);
          setUnread(true);
        }
      )
      .subscribe();

    // Cleanup on unmount
    return () => {
      supabase.removeChannel(userSub);
      supabase.removeChannel(feedbackSub);
    };
  }, []);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = () => setDropdownOpen(false);
    document.addEventListener("click", handleClickOutside);
    return () => document.removeEventListener("click", handleClickOutside);
  }, []);

  return (
    <div className="notification-wrapper">
      <div
        className="notification-bell"
        onClick={(e) => {
          e.stopPropagation();
          setDropdownOpen(!dropdownOpen);
          setUnread(false);
        }}
      >
        <FiBell size={22} />
        {unread && <span className="notification-dot" />}
      </div>

      {dropdownOpen && (
        <div
          className="notification-dropdown"
          onClick={(e) => e.stopPropagation()}
        >
          <div className="dropdown-header">Notifications</div>
          {notifications.length === 0 ? (
            <div className="dropdown-empty">No notifications yet</div>
          ) : (
            notifications.map((note, idx) => (
              <div key={idx} className="dropdown-item">
                <div className="note-message">{note.message}</div>
                <div className="note-time">{note.time}</div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}

export default Notifications;
