import React, { useState, useEffect, useRef } from "react";
import { supabase } from "../Supabase/supabaseClient";
import "../Style/Applications.css";
import { FiMenu, FiBell } from "react-icons/fi";
import { useNavigate } from "react-router-dom";
import Sidebar from "./Sidebar"; // âœ… import Sidebar component

function Applications() {
  const [businesses, setBusinesses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [history, setHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);
  const [selectedBiz, setSelectedBiz] = useState(null);
  const [preview, setPreview] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // ðŸ”§ Settings / Notifications
  const [showSettings, setShowSettings] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  // Reject Modal states
  const [showRejectModal, setShowRejectModal] = useState(false);
  const [reason, setReason] = useState("");
  const [specificReason, setSpecificReason] = useState("");

  // Image handling states
  const [imageErrors, setImageErrors] = useState({});
  const [imageLoading, setImageLoading] = useState({});

  const navigate = useNavigate();
  const searchRef = useRef(null);

  // helper to get file url
  const getFileUrl = (path, bucketName = "businessdocuments") => {
    if (!path) return null;
    if (path.startsWith("http")) return path;

    const { data } = supabase.storage.from(bucketName).getPublicUrl(path);
    return data?.publicUrl || null;
  };

  const handleImageLoad = (key) =>
    setImageLoading((prev) => ({ ...prev, [key]: false }));
  const handleImageLoadStart = (key) =>
    setImageLoading((prev) => ({ ...prev, [key]: true }));
  const handleImageError = (key) =>
    setImageErrors((prev) => ({ ...prev, [key]: true }));

  const renderDocumentImage = (url, alt, key) => {
    const imageUrl = getFileUrl(url);
    const isLoading = imageLoading[key];
    const hasError = imageErrors[key];

    if (!url) return <span>No file uploaded</span>;
    if (!imageUrl) return <span className="error-text">Invalid image URL</span>;
    if (hasError)
      return (
        <div className="error-text">
          Failed to load image
          <br />
          <small>{imageUrl}</small>
        </div>
      );

    return (
      <>
        {isLoading && <div className="image-loading">Loading...</div>}
        <img
          src={imageUrl}
          alt={alt}
          className="doc-img"
          style={{ display: isLoading ? "none" : "block" }}
          onClick={() => setPreview(imageUrl)}
          onLoad={() => handleImageLoad(key)}
          onLoadStart={() => handleImageLoadStart(key)}
          onError={() => handleImageError(key)}
        />
      </>
    );
  };

  useEffect(() => {
    fetchBusinesses();
  }, []);

  const fetchBusinesses = async () => {
    setLoading(true);
    const { data, error } = await supabase.from("business_profiles").select("*");
    if (!error) setBusinesses(data);
    setLoading(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter" && search.trim() !== "") {
      if (!history.includes(search))
        setHistory([search, ...history].slice(0, 5));
      setShowHistory(false);
    }
  };
  const deleteHistory = (item) =>
    setHistory(history.filter((h) => h !== item));

  const filteredBusinesses = businesses.filter((biz) =>
    [
      biz.business_name,
      biz.owner_first_name,
      biz.owner_last_name,
      biz.email,
      biz.business_address,
      biz.business_phone_number,
    ]
      .filter(Boolean)
      .some((field) => field.toLowerCase().includes(search.toLowerCase()))
  );

  // Approve/Reject
  const handleApprove = async () => {
    if (!selectedBiz) return;
    const { data, error } = await supabase
      .from("business_profiles")
      .update({ status: "approved" })
      .eq("id", selectedBiz.id)
      .select();
    if (!error) {
      setBusinesses((prev) =>
        prev.map((b) => (b.id === selectedBiz.id ? { ...b, status: "approved" } : b))
      );
      setSelectedBiz(null);
    }
  };

  const handleReject = async () => {
    if (!selectedBiz) return;
    await supabase
      .from("business_profiles")
      .update({
        status: "rejected",
        rejection_reason: reason,
        rejection_notes: specificReason,
      })
      .eq("id", selectedBiz.id);
    fetchBusinesses();
    setSelectedBiz(null);
    setShowRejectModal(false);
    setReason("");
    setSpecificReason("");
  };

  return (
    <div className="dashboard-root">
      {/* âœ… Sidebar replaced */}
      <Sidebar
        active="applications"
        sidebarOpen={sidebarOpen}
        onLogout={() => navigate("/logout")}
      />

      <main className={`dashboard-main ${sidebarOpen ? "" : "expanded"}`}>
        <div className="applications-container">
          <header className="applications-header">
            <div className="header-left">
              <FiMenu
                className="toggle-sidebar"
                onClick={() => setSidebarOpen(!sidebarOpen)}
              />
              <div>
                <h2>APPLICATIONS</h2>
                <p className="applications-subtitle">
                  All the Applicants Businesses waiting to be reviewed and Approved
                </p>
              </div>
            </div>
            <div className="dashboard-header-icons">
              <div className="dropdown-wrapper">
                <FiBell
                  className="icon"
                  onClick={() => {
                    setShowNotifications(!showNotifications);
                    setShowSettings(false);
                    setUnreadCount(0);
                  }}
                />
                {unreadCount > 0 && (
                  <span className="notification-badge">{unreadCount}</span>
                )}
                {showNotifications && (
                  <div className="dropdown-panel">
                    {notifications.length === 0 ? (
                      <div className="dropdown-item">No notifications</div>
                    ) : (
                      notifications.map((n) => (
                        <div key={n.id} className="dropdown-item">
                          <strong>{n.title}</strong>
                          <p>{n.message}</p>
                          <span className="notif-time">{n.time}</span>
                        </div>
                      ))
                    )}
                    <div
                      className="dropdown-item"
                      onClick={() => navigate("/notifications")}
                    >
                      View All
                    </div>
                  </div>
                )}
              </div>
              <div className="dropdown-wrapper">
                <img
                  src="https://via.placeholder.com/32"
                  alt="profile"
                  className="profile-avatar"
                  onClick={() => {
                    setShowSettings(!showSettings);
                    setShowNotifications(false);
                  }}
                />
                {showSettings && (
                  <div className="dropdown-panel">
                    <div
                      className="dropdown-item"
                      onClick={() => navigate("/profile")}
                    >
                      My Profile
                    </div>
                    <div
                      className="dropdown-item"
                      onClick={() => navigate("/settings")}
                    >
                      Settings
                    </div>
                    <div
                      className="dropdown-item"
                      onClick={() => alert("Logging out...")}
                    >
                      Logout
                    </div>
                  </div>
                )}
              </div>
            </div>
          </header>

          {!selectedBiz ? (
            <>
              {/* Filters */}
              <div className="applications-filters">
                <div className="filter-tab">
                  All Applicants{" "}
                  <span className="count">{filteredBusinesses.length}</span>
                </div>
                <div className="search-wrapper" ref={searchRef}>
                  <input
                    type="text"
                    placeholder="Search Here"
                    className="search-box"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    onKeyDown={handleKeyDown}
                    onFocus={() => setShowHistory(true)}
                  />
                  {showHistory && history.length > 0 && (
                    <ul className="search-history">
                      {history.map((item, idx) => (
                        <li key={idx} onClick={() => setSearch(item)}>
                          <span>{item}</span>
                          <button
                            className="delete-history"
                            onClick={(e) => {
                              e.stopPropagation();
                              deleteHistory(item);
                            }}
                          >
                            Ã—
                          </button>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>

              <div className="applications-table-wrapper">
                <h3 className="table-title">Applicants For Review</h3>
                <table className="applications-table">
                  <thead>
                    <tr>
                      <th>Review</th>
                      <th>Store Name</th>
                      <th>Owner Name</th>
                      <th>Phone Number</th>
                      <th>Date Submitted</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {loading ? (
                      <tr>
                        <td colSpan="6">Loading...</td>
                      </tr>
                    ) : filteredBusinesses.length > 0 ? (
                      filteredBusinesses.map((biz) => (
                        <tr key={biz.id}>
                          <td>
                            <button
                              className="review-btn"
                              onClick={() => setSelectedBiz(biz)}
                            >
                              Review
                            </button>
                          </td>
                          <td>{biz.business_name}</td>
                          <td>
                            {biz.owner_first_name} {biz.owner_last_name}
                          </td>
                          <td>{biz.business_phone_number}</td>
                          <td>
                            {new Date(biz.created_at).toLocaleDateString()}
                          </td>
                          <td className={`status ${biz.status || "pending"}`}>
                            {biz.status || "Pending"}
                          </td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="6">No businesses found.</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </>
          ) : (
            <div className="review-panel">
              <h3>{selectedBiz.business_name} For Review</h3>
              {/* details */}
              <p>
                <strong>Owner:</strong> {selectedBiz.owner_first_name}{" "}
                {selectedBiz.owner_last_name}
              </p>
              <p>
                <strong>Phone:</strong> {selectedBiz.business_phone_number}
              </p>
              <div className="review-docs">
                <div className="doc-box">
                  <p>BIR Registration</p>
                  {renderDocumentImage(
                    selectedBiz.bir_registration_url,
                    "BIR",
                    "bir"
                  )}
                </div>
                <div className="doc-box">
                  <p>Business Certificate</p>
                  {renderDocumentImage(
                    selectedBiz.business_certificate_url,
                    "Certificate",
                    "certificate"
                  )}
                </div>
                <div className="doc-box">
                  <p>Mayor Permit</p>
                  {renderDocumentImage(
                    selectedBiz.mayors_permit_url,
                    "Mayor",
                    "mayor"
                  )}
                </div>
              </div>
              <div className="review-actions">
                <button className="approve-btn" onClick={handleApprove}>
                  Approve
                </button>
                <button
                  className="reject-btn"
                  onClick={() => setShowRejectModal(true)}
                >
                  Reject
                </button>
                <button
                  className="back-btn"
                  onClick={() => setSelectedBiz(null)}
                >
                  Back
                </button>
              </div>
            </div>
          )}
        </div>
      </main>

      {preview && (
        <div className="preview-overlay" onClick={() => setPreview(null)}>
          <div className="preview-content">
            <img src={preview} alt="Document Preview" />
          </div>
        </div>
      )}

      {showRejectModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>Rejecting This Applicant</h3>
              <button
                className="close-btn"
                onClick={() => setShowRejectModal(false)}
              >
                Ã—
              </button>
            </div>
            <div className="modal-body">
              <h4>Reason for Rejection</h4>
              {[
                "Incomplete Documents",
                "Invalid Documents",
                "Duplicate Registration",
                "Unclear Photo of documentation",
              ].map((r) => (
                <label key={r}>
                  <input
                    type="radio"
                    name="reason"
                    value={r}
                    checked={reason === r}
                    onChange={(e) => setReason(e.target.value)}
                  />
                  {r}
                </label>
              ))}
              <h4>Specific Reason</h4>
              <textarea
                placeholder="Write your Reason why this client is rejected."
                value={specificReason}
                onChange={(e) => setSpecificReason(e.target.value)}
              />
            </div>
            <div className="modal-footer">
              <button className="reject-confirm" onClick={handleReject}>
                Reject
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Applications;
