import React, { useEffect, useState } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

function Profile() {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState({
    first_name: "",
    last_name: "",
    phone: "",
    facebook: "",
    twitter: "",
    linkedin: "",
    website: ""
  });
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();
      setUser(user);

      if (user) {
        const { data, error } = await supabase
          .from("user_profiles")
          .select("*")
          .eq("id", user.id)
          .single();

        if (!error && data) {
          setProfile({
            first_name: data.first_name || "",
            last_name: data.last_name || "",
            phone: data.phone || "",
            facebook: data.facebook || "",
            twitter: data.twitter || "",
            linkedin: data.linkedin || "",
            website: data.website || ""
          });
        }
      }
      setLoading(false);
    };
    fetchProfile();
  }, []);

  const handleUpdate = async () => {
    if (!user) return;
    const { error } = await supabase
      .from("user_profiles")
      .update(profile)
      .eq("id", user.id);

    if (error) {
      setMessage("‚ùå Update failed: " + error.message);
    } else {
      setMessage("‚úÖ Profile updated successfully!");
    }
  };

  if (loading) {
    return <div className="page-container"><p>‚è≥ Loading profile...</p></div>;
  }

  return (
    <div className="page-container">
      {/* Back Button */}
      <button className="btn" onClick={() => navigate(-1)}>‚¨Ö Back</button>

      <h2>üë§ My Profile</h2>

      <div className="card profile-header">
        <div className="profile-avatar">
          {user?.email ? (
            <div className="avatar-img">
              {profile.first_name
                ? profile.first_name[0].toUpperCase()
                : user.email[0].toUpperCase()}
            </div>
          ) : (
            <img
              src="https://via.placeholder.com/80"
              alt="avatar"
              className="avatar-img"
            />
          )}
        </div>
        <div>
          <p><strong>Email:</strong> {user?.email}</p>
          <p><strong>User ID:</strong> {user?.id}</p>
        </div>
      </div>

      {/* Personal Information */}
      <div className="card">
        <h3>üìå Personal Information</h3>
        <div className="form-group">
          <label>First Name</label>
          <input
            type="text"
            value={profile.first_name}
            onChange={(e) => setProfile({ ...profile, first_name: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label>Last Name</label>
          <input
            type="text"
            value={profile.last_name}
            onChange={(e) => setProfile({ ...profile, last_name: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input
            type="text"
            value={profile.phone}
            onChange={(e) => setProfile({ ...profile, phone: e.target.value })}
          />
        </div>
      </div>

      {/* Social Media */}
      <div className="card">
        <h3>üåê Social Media Accounts</h3>
        <div className="form-group">
          <label>Facebook</label>
          <input
            type="text"
            value={profile.facebook}
            onChange={(e) => setProfile({ ...profile, facebook: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label>Twitter</label>
          <input
            type="text"
            value={profile.twitter}
            onChange={(e) => setProfile({ ...profile, twitter: e.target.value })}
          />
        </div>
        <div className="form-group">
          <label>LinkedIn</label>
          <input
            type="text"
            value={profile.linkedin}
            onChange={(e) => setProfile({ ...profile, linkedin: e.target.value })}
          />
        </div>
        <button className="btn primary" disabled>+ Add Social Media</button>
      </div>

      {/* Other Info */}
      <div className="card">
        <h3>üìù Other</h3>
        <div className="form-group">
          <label>Website</label>
          <input
            type="text"
            value={profile.website}
            onChange={(e) => setProfile({ ...profile, website: e.target.value })}
          />
        </div>
      </div>

      <button className="btn primary" onClick={handleUpdate}>
        Save Profile
      </button>
      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Profile;
