import React, { useState, useEffect } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

// Simple inline translations
const translations = {
  en: {
    back: "‚¨Ö Back",
    title: "‚öôÔ∏è Admin Settings",
    security: "üîë Security",
    newPassword: "New Password",
    twoFA: "Enable Two-Factor Authentication (2FA)",
    updatePassword: "Update Password",
    preferences: "üé® Preferences",
    theme: "Theme",
    language: "Language",
    emailNotif: "Enable Email Notifications",
    userManagement: "üë• User Management",
    defaultRole: "Default Role for New Users",
    customer: "Customer",
    owner: "Business Owner",
    admin: "Admin",
    systemControls: "üõ† System Controls",
    maintenanceMode: "Enable Maintenance Mode",
    saveSettings: "Save Settings",
  },
  ph: {
    back: "‚¨Ö Bumalik",
    title: "‚öôÔ∏è Mga Setting ng Admin",
    security: "üîë Seguridad",
    newPassword: "Bagong Password",
    twoFA: "I-enable ang Dalawang Hakbang na Pagpapatunay (2FA)",
    updatePassword: "I-update ang Password",
    preferences: "üé® Mga Kagustuhan",
    theme: "Tema",
    language: "Wika",
    emailNotif: "I-enable ang Email Notifications",
    userManagement: "üë• Pamamahala ng User",
    defaultRole: "Default na Role para sa Bagong User",
    customer: "Customer",
    owner: "May-ari ng Negosyo",
    admin: "Admin",
    systemControls: "üõ† Mga Kontrol ng Sistema",
    maintenanceMode: "I-enable ang Maintenance Mode",
    saveSettings: "I-save ang Settings",
  },
};

function Settings() {
  const [password, setPassword] = useState("");
  const [theme, setTheme] = useState("light");
  const [notifications, setNotifications] = useState(true);
  const [maintenanceMode, setMaintenanceMode] = useState(false);
  const [twoFactorAuth, setTwoFactorAuth] = useState(false);
  const [defaultRole, setDefaultRole] = useState("customer");
  const [language, setLanguage] = useState("en");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  // Load saved settings on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);

    const savedRole = localStorage.getItem("defaultRole") || "customer";
    setDefaultRole(savedRole);

    const savedLang = localStorage.getItem("language") || "en";
    setLanguage(savedLang);
  }, []);

  // Save settings
  const handleSaveSettings = () => {
    document.documentElement.setAttribute("data-theme", theme);
    localStorage.setItem("theme", theme);
    localStorage.setItem("defaultRole", defaultRole);
    localStorage.setItem("language", language);

    setMessage("‚úÖ Settings saved & applied globally!");
  };

  // Update password
  const handlePasswordUpdate = async () => {
    if (!password.trim()) {
      setMessage("‚ùå Please enter a new password.");
      return;
    }

    const { error } = await supabase.auth.updateUser({ password });
    if (error) {
      setMessage("‚ùå Error: " + error.message);
    } else {
      setMessage("‚úÖ Password updated successfully!");
      setPassword("");
    }
  };

  // shortcut to translations
  const t = translations[language];

  return (
    <div className="page-container">
      {/* Back Button */}
      <button className="btn" onClick={() => navigate(-1)}>
        {t.back}
      </button>

      <h2>{t.title}</h2>

      {/* Security */}
      <div className="card">
        <h3>{t.security}</h3>
        <div className="form-group">
          <label>{t.newPassword}</label>
          <input
            type="password"
            placeholder={t.newPassword}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={twoFactorAuth}
            onChange={() => setTwoFactorAuth(!twoFactorAuth)}
          />
          <label>{t.twoFA}</label>
        </div>
        <button className="btn danger" onClick={handlePasswordUpdate}>
          {t.updatePassword}
        </button>
      </div>

      {/* Preferences */}
      <div className="card">
        <h3>{t.preferences}</h3>
        <div className="form-group">
          <label>{t.theme}</label>
          <select value={theme} onChange={(e) => setTheme(e.target.value)}>
            <option value="light">üåû Light</option>
            <option value="dark">üåô Dark</option>
          </select>
        </div>
        <div className="form-group">
          <label>{t.language}</label>
          <select
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="en">English</option>
            <option value="ph">Filipino</option>
          </select>
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={notifications}
            onChange={() => setNotifications(!notifications)}
          />
          <label>{t.emailNotif}</label>
        </div>
      </div>

      {/* User Management */}
      <div className="card">
        <h3>{t.userManagement}</h3>
        <div className="form-group">
          <label>{t.defaultRole}</label>
          <select
            value={defaultRole}
            onChange={(e) => setDefaultRole(e.target.value)}
          >
            <option value="customer">{t.customer}</option>
            <option value="owner">{t.owner}</option>
            <option value="admin">{t.admin}</option>
          </select>
        </div>
      </div>

      {/* System Controls */}
      <div className="card">
        <h3>{t.systemControls}</h3>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={maintenanceMode}
            onChange={() => setMaintenanceMode(!maintenanceMode)}
          />
          <label>{t.maintenanceMode}</label>
        </div>
        <button className="btn primary" onClick={handleSaveSettings}>
          {t.saveSettings}
        </button>
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Settings;
