import React, { useState, useEffect } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

function Settings() {
  const [password, setPassword] = useState("");
  const [theme, setTheme] = useState("light");
  const [notifications, setNotifications] = useState(true);
  const [maintenanceMode, setMaintenanceMode] = useState(false);
  const [twoFactorAuth, setTwoFactorAuth] = useState(false);
  const [defaultRole, setDefaultRole] = useState("customer");
  const [language, setLanguage] = useState("en");
  const [message, setMessage] = useState("");

  const navigate = useNavigate();

  // Load saved settings on mount
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.documentElement.setAttribute("data-theme", savedTheme);

    const savedRole = localStorage.getItem("defaultRole") || "customer";
    setDefaultRole(savedRole);

    const savedLang = localStorage.getItem("language") || "en";
    setLanguage(savedLang);
  }, []);

  // Save settings to localStorage and apply globally
  const handleSaveSettings = () => {
    document.documentElement.setAttribute("data-theme", theme);
    localStorage.setItem("theme", theme);
    localStorage.setItem("defaultRole", defaultRole);
    localStorage.setItem("language", language);

    setMessage("‚úÖ Settings saved & applied globally!");
  };

  // Update password
  const handlePasswordUpdate = async () => {
    if (!password.trim()) {
      setMessage("‚ùå Please enter a new password.");
      return;
    }

    const { error } = await supabase.auth.updateUser({ password });
    if (error) {
      setMessage("‚ùå Error: " + error.message);
    } else {
      setMessage("‚úÖ Password updated successfully!");
      setPassword("");
    }
  };

  return (
    <div className="page-container">
      {/* Back Button */}
      <button className="btn" onClick={() => navigate(-1)}>
        ‚¨Ö Back
      </button>

      <h2>‚öôÔ∏è Admin Settings</h2>

      {/* Security */}
      <div className="card">
        <h3>üîë Security</h3>
        <div className="form-group">
          <label>New Password</label>
          <input
            type="password"
            placeholder="Enter new password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={twoFactorAuth}
            onChange={() => setTwoFactorAuth(!twoFactorAuth)}
          />
          <label>Enable Two-Factor Authentication (2FA)</label>
        </div>
        <button className="btn danger" onClick={handlePasswordUpdate}>
          Update Password
        </button>
      </div>

      {/* Preferences */}
      <div className="card">
        <h3>üé® Preferences</h3>
        <div className="form-group">
          <label>Theme</label>
          <select value={theme} onChange={(e) => setTheme(e.target.value)}>
            <option value="light">üåû Light</option>
            <option value="dark">üåô Dark</option>
          </select>
        </div>
        <div className="form-group">
          <label>Language</label>
          <select value={language} onChange={(e) => setLanguage(e.target.value)}>
            <option value="en">English</option>
            <option value="ph">Filipino</option>
          </select>
        </div>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={notifications}
            onChange={() => setNotifications(!notifications)}
          />
          <label>Enable Email Notifications</label>
        </div>
      </div>

      {/* User Management */}
      <div className="card">
        <h3>üë• User Management</h3>
        <div className="form-group">
          <label>Default Role for New Users</label>
          <select
            value={defaultRole}
            onChange={(e) => setDefaultRole(e.target.value)}
          >
            <option value="customer">Customer</option>
            <option value="owner">Business Owner</option>
            <option value="admin">Admin</option>
          </select>
        </div>
      </div>

      {/* System Controls */}
      <div className="card">
        <h3>üõ† System Controls</h3>
        <div className="form-group checkbox">
          <input
            type="checkbox"
            checked={maintenanceMode}
            onChange={() => setMaintenanceMode(!maintenanceMode)}
          />
          <label>Enable Maintenance Mode</label>
        </div>
        <button className="btn primary" onClick={handleSaveSettings}>
          Save Settings
        </button>
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Settings;
