// src/Components/settings/Profile.js
import React, { useState } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

function Profile({ adminUser }) {
  const isDefaultAdmin = !adminUser?.id; // no id = default login

  const [form, setForm] = useState({
    username: adminUser?.username || "",
    password: adminUser?.password || "",
    confirmPassword: "",
    email: adminUser?.email || "",
    phone_number: adminUser?.phone_number || "",
    profile_img: adminUser?.profile_img || "",
    otp: "",
  });

  const [verificationSent, setVerificationSent] = useState(false);
  const [verified, setVerified] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Send OTP to email
  const sendOtp = async () => {
    if (!form.email) {
      setMessage("❌ Please enter an email.");
      return;
    }
    setLoading(true);

    const { error } = await supabase.auth.signInWithOtp({
      email: form.email,
      options: { emailRedirectTo: window.location.origin },
    });

    setLoading(false);
    if (error) {
      setMessage("❌ Failed to send OTP: " + error.message);
    } else {
      setMessage("📩 OTP sent to " + form.email);
      setVerificationSent(true);
    }
  };

  // Verify OTP
  const verifyOtp = async () => {
    if (!form.otp) {
      setMessage("❌ Please enter the OTP.");
      return;
    }
    setLoading(true);

    const { data, error } = await supabase.auth.verifyOtp({
      email: form.email,
      token: form.otp,
      type: "magiclink", // Supabase requires type, for OTP it's still "magiclink"
    });

    setLoading(false);

    if (error) {
      setMessage("❌ OTP verification failed: " + error.message);
    } else {
      setMessage("✅ Email verified successfully!");
      setVerified(true);
    }
  };

  // Save profile
  const handleSave = async () => {
    if (!verified) {
      setMessage("❌ Please verify your email before saving.");
      return;
    }
    if (form.password !== form.confirmPassword) {
      setMessage("❌ Passwords do not match.");
      return;
    }

    setLoading(true);
    let error;

    if (isDefaultAdmin) {
      const { error: insertError } = await supabase.from("admin").insert([
        {
          username: form.username,
          password: form.password, // ⚠️ hash in production
          email: form.email,
          phone_number: form.phone_number,
          profile_img: form.profile_img,
        },
      ]);
      error = insertError;
    } else {
      const { error: updateError } = await supabase
        .from("admin")
        .update({
          username: form.username,
          password: form.password,
          email: form.email,
          phone_number: form.phone_number,
          profile_img: form.profile_img,
        })
        .eq("id", adminUser.id);
      error = updateError;
    }

    setLoading(false);
    if (error) {
      setMessage("❌ Save failed: " + error.message);
    } else {
      setMessage("✅ Profile saved! Use new credentials next login.");
      setTimeout(() => navigate("/dashboard"), 1500);
    }
  };

  // Upload profile image
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const filePath = `avatars/${Date.now()}_${file.name}`;
    const { error } = await supabase.storage.from("admin-avatars").upload(filePath, file);

    if (error) {
      setMessage("❌ Upload failed: " + error.message);
    } else {
      const { data } = supabase.storage.from("admin-avatars").getPublicUrl(filePath);
      setForm({ ...form, profile_img: data.publicUrl });
      setMessage("✅ Profile picture uploaded!");
    }
  };

  return (
    <div className="page-container">
      <button className="btn" onClick={() => navigate(-1)}>⬅ Back</button>
      <h2>👤 Admin Profile</h2>

      <div className="card">
        {/* Avatar */}
        <div className="form-group">
          <label>Profile Picture</label>
          <input type="file" accept="image/*" onChange={handleFileUpload} />
          {form.profile_img && (
            <img src={form.profile_img} alt="Avatar" className="avatar-preview" />
          )}
        </div>

        <div className="form-group">
          <label>Username</label>
          <input type="text" value={form.username}
            onChange={(e) => setForm({ ...form, username: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Confirm Password</label>
          <input type="password" value={form.confirmPassword}
            onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })} disabled={verified} />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input type="text" value={form.phone_number}
            onChange={(e) => setForm({ ...form, phone_number: e.target.value })} />
        </div>

        {!verificationSent && (
          <button className="btn primary" onClick={sendOtp} disabled={loading}>
            {loading ? "Sending..." : "Send OTP"}
          </button>
        )}

        {verificationSent && !verified && (
          <>
            <div className="form-group">
              <label>Enter OTP</label>
              <input type="text" value={form.otp}
                onChange={(e) => setForm({ ...form, otp: e.target.value })} />
            </div>
            <button className="btn primary" onClick={verifyOtp} disabled={loading}>
              {loading ? "Verifying..." : "Verify OTP"}
            </button>
          </>
        )}

        {verified && (
          <button className="btn primary" onClick={handleSave} disabled={loading}>
            {loading ? "Saving..." : "Save Profile"}
          </button>
        )}
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Profile;
