import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { supabase } from "../../Supabase/supabaseClient";
import "../../Style/Details/ClientDetails.css";

function ClientDetail() {
  const { id } = useParams();
  const [client, setClient] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClient = async () => {
      const { data, error } = await supabase
        .from("business_profiles")
        .select("*")
        .eq("id", id)
        .single();

      if (error) {
        console.error("Error fetching client:", error.message);
      } else {
        setClient(data);
      }
      setLoading(false);
    };

    fetchClient();
  }, [id]);

  if (loading) return <p>Loading details...</p>;
  if (!client) return <p>No client found.</p>;

  return (
    <div className="client-detail">
      <button onClick={() => navigate(-1)} className="back-btn">
        ‚¨Ö Back
      </button>

      <div className="client-header">
        <img
          src={client.cover_photo_url || "https://via.placeholder.com/600x300"}
          alt={client.business_name}
          className="cover-photo"
        />
        <h1>{client.business_name || client.name}</h1>
        <p className="address">üìç {client.business_address}</p>
        <p className="since">
          Member Since{" "}
          {client?.created_at
            ? new Date(client.created_at).toLocaleDateString()
            : "N/A"}
        </p>
      </div>

      <div className="client-body">
        <h2>About</h2>
        <p>{client.description || "No description available."}</p>

        <h2>Contact Info</h2>
        <p>üìû {client.contact_number || "N/A"}</p>
        <p>üìß {client.email || "N/A"}</p>

        <h2>Owner</h2>
        <p>{client.owner_name || "N/A"}</p>
      </div>
    </div>
  );
}

export default ClientDetail;
