import React, { useEffect, useState } from "react";
import { supabase } from "../Supabase/supabaseClient";
import "../Style/Users.css";
import {
  FiBell,
  FiMenu,
  FiHome,
  FiFileText,
  FiUsers,
  FiUser,
  FiClock,
  FiMessageSquare,
  FiLogOut,
  FiMoreVertical,
} from "react-icons/fi";
import { Link, useNavigate } from "react-router-dom";

const statusColor = (status) =>
  status === "Verified" ? { color: "green" } : { color: "red" };

function Users() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [menuOpen, setMenuOpen] = useState(null);
  const [sidebarOpen, setSidebarOpen] = useState(true);

  // 🔔 Notifications & Profile states
  const [showSettings, setShowSettings] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUsers = async () => {
      setLoading(true);

      // 1. Fetch from user_profiles
      const { data: profiles, error: profileError } = await supabase
        .from("user_profiles")
        .select("*");

      // 2. Fetch from Supabase Auth (needs service role or admin API)
      const { data: authUsers, error: authError } = await supabase.auth.admin.listUsers();

      if (profileError || authError) {
        console.error("Error fetching users:", profileError || authError);
        setLoading(false);
        return;
      }

      // 3. Map auth verification status into profiles
      const authMap = {};
      authUsers.users.forEach((authUser) => {
        authMap[authUser.email] = !!authUser.email_confirmed_at; // true if verified
      });

      const mergedUsers = profiles.map((user) => ({
        ...user,
        verified_status: authMap[user.email] ? "Verified" : "Not-Verified",
      }));

      setUsers(mergedUsers);
      setLoading(false);
    };

    fetchUsers();
  }, []);

  // ✅ Filter users
  const filteredUsers = users.filter((u) =>
    [u.first_name, u.last_name, u.email, u.mobile_number, u.customer_id]
      .filter(Boolean)
      .some((f) => f.toLowerCase().includes(search.toLowerCase()))
  );

  const toggleMenu = (idx) => {
    setMenuOpen(menuOpen === idx ? null : idx);
  };

  return (
    <div className="dashboard-root">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? "" : "closed"}`}>
        <div className="sidebar-title">Laundry Scout</div>
        <nav>
          <ul>
            <li>
              <Link to="/dashboard">
                <FiHome className="menu-icon" />
                <span>Dashboard</span>
              </Link>
            </li>
            <li>
              <Link to="/applications">
                <FiFileText className="menu-icon" />
                <span>Applications</span>
              </Link>
            </li>
            <li>
              <Link to="/clients">
                <FiUsers className="menu-icon" />
                <span>Clients</span>
              </Link>
            </li>
            <li className="active">
              <Link to="/users">
                <FiUser className="menu-icon" />
                <span>Users</span>
              </Link>
            </li>
            <li>
              <Link to="/history">
                <FiClock className="menu-icon" />
                <span>History</span>
              </Link>
            </li>
            <li>
              <Link to="/feedback">
                <FiMessageSquare className="menu-icon" />
                <span>Feedback</span>
              </Link>
            </li>
          </ul>
        </nav>
        <div className="logout" onClick={() => navigate("/logout")}>
          <FiLogOut className="menu-icon" />
          <span>Log Out</span>
        </div>
      </aside>

      {/* Main */}
      <main className="dashboard-main">
        <div className="users-container">
          {/* 🔝 Top Header Bar */}
          <div className="users-header">
            <div>
              <h2 className="users-title">Users</h2>
              <p>All the users Using the App can be viewed here</p>
            </div>
          </div>

          {/* 🔍 Filter/Search Bar */}
          <div className="users-filters">
            <div className="filter-tab">
              All Users <span className="count">{filteredUsers.length}</span>
            </div>
            <input
              type="text"
              placeholder="Search Here"
              className="search-box"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
          </div>

          {/* ✅ Table */}
          <div className="users-table-wrapper">
            <h3 className="user-profiles-title">User Profiles</h3>
            <table className="users-table">
              <thead>
                <tr>
                  <th>Customer ID</th>
                  <th>FirstName</th>
                  <th>LastName</th>
                  <th>Mobile Number</th>
                  <th>Email</th>
                  <th>Update Date</th>
                  <th>Verified Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="8">Loading...</td>
                  </tr>
                ) : filteredUsers.length > 0 ? (
                  filteredUsers.map((user, idx) => (
                    <tr key={user.uid || idx}>
                      <td>{user.customer_id || `000${idx + 1}`}</td>
                      <td>{user.first_name}</td>
                      <td>{user.last_name}</td>
                      <td>{user.mobile_number}</td>
                      <td>{user.email}</td>
                      <td>
                        {user.created_at
                          ? new Date(user.created_at).toLocaleDateString(
                              "en-US",
                              {
                                year: "numeric",
                                month: "long",
                                day: "numeric",
                              }
                            )
                          : ""}
                      </td>
                      <td style={statusColor(user.verified_status)}>
                        {user.verified_status}
                      </td>
                      <td className="actions-cell">
                        <button
                          className="menu-btn"
                          onClick={() => toggleMenu(idx)}
                        >
                          <FiMoreVertical />
                        </button>
                        {menuOpen === idx && (
                          <div className="menu-dropdown">
                            <button
                              onClick={() => alert(`Viewing ${user.email}`)}
                            >
                              View
                            </button>
                            <button
                              onClick={() => alert(`Editing ${user.email}`)}
                            >
                              Edit
                            </button>
                            <button
                              onClick={() => alert(`Deleting ${user.email}`)}
                              className="danger"
                            >
                              Delete
                            </button>
                          </div>
                        )}
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="8">No users found.</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </main>
    </div>
  );
}

export default Users;
