// src/Components/settings/Profile.js
import React, { useState } from "react";
import { supabase } from "../../Supabase/supabaseClient";
import { useNavigate } from "react-router-dom";
import "../../Style/Admin.css";

function Profile({ adminUser }) {
  const isDefaultAdmin = !adminUser?.id; // no id = default login (not yet in DB)

  const [form, setForm] = useState({
    username: adminUser?.username || "",
    password: adminUser?.password || "",
    confirmPassword: "",
    email: adminUser?.email || "",
    phone_number: adminUser?.phone_number || "",
    profile_img: adminUser?.profile_img || "",
  });

  const [verificationSent, setVerificationSent] = useState(false);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // ‚úÖ Send verification link
  const sendVerificationLink = async () => {
    if (!form.email) {
      setMessage("‚ùå Please enter an email.");
      return;
    }
    setLoading(true);
    const { error } = await supabase.auth.signInWithOtp({ email: form.email });
    setLoading(false);

    if (error) {
      setMessage("‚ùå Failed to send verification email: " + error.message);
    } else {
      setMessage("üì© Verification link sent to " + form.email);
      setVerificationSent(true);
    }
  };

  // ‚úÖ Save profile (insert if default admin, else update)
  const handleSave = async () => {
    if (!verificationSent) {
      setMessage("‚ùå Please verify your email before saving.");
      return;
    }
    if (form.password !== form.confirmPassword) {
      setMessage("‚ùå Passwords do not match.");
      return;
    }

    setLoading(true);
    let error;

    if (isDefaultAdmin) {
      // insert new admin row
      const { error: insertError } = await supabase.from("admin").insert([
        {
          username: form.username,
          password: form.password, // ‚ö†Ô∏è hash in production
          email: form.email,
          phone_number: form.phone_number,
          profile_img: form.profile_img,
        },
      ]);
      error = insertError;
    } else {
      // update existing admin row
      const { error: updateError } = await supabase
        .from("admin")
        .update({
          username: form.username,
          password: form.password,
          email: form.email,
          phone_number: form.phone_number,
          profile_img: form.profile_img,
        })
        .eq("id", adminUser.id);
      error = updateError;
    }

    setLoading(false);
    if (error) {
      setMessage("‚ùå Save failed: " + error.message);
    } else {
      setMessage("‚úÖ Profile saved! Use new credentials next login.");
      setTimeout(() => navigate("/dashboard"), 1500);
    }
  };

  // ‚úÖ Upload profile image to Supabase storage
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const filePath = `avatars/${Date.now()}_${file.name}`;
    const { error } = await supabase.storage
      .from("admin-avatars")
      .upload(filePath, file);

    if (error) {
      setMessage("‚ùå Upload failed: " + error.message);
    } else {
      const { data } = supabase.storage
        .from("admin-avatars")
        .getPublicUrl(filePath);
      setForm({ ...form, profile_img: data.publicUrl });
      setMessage("‚úÖ Profile picture uploaded!");
    }
  };

  return (
    <div className="page-container">
      <button className="btn" onClick={() => navigate(-1)}>‚¨Ö Back</button>
      <h2>üë§ Admin Profile</h2>

      <div className="card">
        {/* Avatar */}
        <div className="form-group">
          <label>Profile Picture</label>
          <input type="file" accept="image/*" onChange={handleFileUpload} />
          {form.profile_img && (
            <img src={form.profile_img} alt="Avatar" className="avatar-preview" />
          )}
        </div>

        <div className="form-group">
          <label>Username</label>
          <input type="text" value={form.username}
            onChange={(e) => setForm({ ...form, username: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" value={form.password}
            onChange={(e) => setForm({ ...form, password: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Confirm Password</label>
          <input type="password" value={form.confirmPassword}
            onChange={(e) => setForm({ ...form, confirmPassword: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input type="email" value={form.email}
            onChange={(e) => setForm({ ...form, email: e.target.value })} />
        </div>
        <div className="form-group">
          <label>Phone</label>
          <input type="text" value={form.phone_number}
            onChange={(e) => setForm({ ...form, phone_number: e.target.value })} />
        </div>

        {!verificationSent ? (
          <button className="btn primary" onClick={sendVerificationLink} disabled={loading}>
            {loading ? "Sending..." : "Verify Email"}
          </button>
        ) : (
          <button className="btn primary" onClick={handleSave} disabled={loading}>
            {loading ? "Saving..." : "Save Profile"}
          </button>
        )}
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default Profile;
